import React, { useEffect, useState } from 'react';
import './App.css';
import Counter from './Counter';
import Countdown from 'react-countdown'
import zeroPad from 'react-countdown'


function App() {


  const [sessionTimer,setSessionTimer] = useState(25)
  const timer = (count) => setSessionTimer(count)




  const render = ({ formatted: { hours, minutes, seconds }, completed }) => (<span id='time-left'>{minutes}:{seconds}</span>)

  let time= <Countdown 
                  date={Date.now() + (sessionTimer*60000)} 
                  autoStart={true}
                  renderer={render}
                  />

  return (
    <div className="App">
      <div id='counters'>
        <Counter 
          labelId='break-label' 
          label='Break Length' 
          time={5}
          incrementId='break-increment'
          decrementId='break-decrement'
          lengthId='break-length'

        />
        <Counter 
          labelId='session-label' 
          label='Session Length' 
          time={25}
          incrementId='session-increment'
          decrementId='session-decrement'
          lengthId='session-length'
          timer={timer()}
        />  
      </div>
      <div id="session-container">
        <h2 id='timer-label'>Session</h2>
        {time}

      </div>
    </div>
  );
}

export default App;
