import { useState } from 'react';
import './App.css';

function App() {
  const[breakCount, setBreakCount] = useState(5)
  const[sessionCount, setSessionCount] = useState(25)

  const handleBreakCount = (op) =>{
    op==='up' ? setBreakCount((prev)=>prev+1) : setBreakCount((prev)=>prev-1)
  }
  const resetBreakCount = () => {
    setBreakCount(0)
  }
  const handleSessionCount = (op) =>{
    op==='up' ? setSessionCount((prev)=>prev+1) : setSessionCount((prev)=>prev-1)
  }
  const resetSessionCount = () => {
    setSessionCount(0)
  }
  return (
    <div className="App">
    <div id='break'>
      <p>{breakCount}</p>
      <button onClick={()=>handleBreakCount('up')}>UP</button>
      <button onClick={()=>handleBreakCount('down')}>DOWN</button>
      <button onClick={resetBreakCount}>RESET</button>
    </div>
    <div id='session'>
      <p>{sessionCount}</p>
      <button onClick={()=>handleSessionCount('up')}>UP</button>
      <button onClick={()=>handleSessionCount('down')}>DOWN</button>
      <button onClick={resetSessionCount}>RESET</button>
    </div>

    </div>
  );
}

export default App;
