import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useState } from 'react';
import './App.css';

function App() {
  const[breakCount, setBreakCount] = useState(5)
  const[sessionCount, setSessionCount] = useState(25)

  const handleBreakCount = (op) =>{
    op==='up' ? setBreakCount((prev)=>prev+1) : setBreakCount((prev)=>prev-1)
  }
  const resetBreakCount = () => {
    setBreakCount(5)
  }
  const handleSessionCount = (op) =>{
    op==='up' ? setSessionCount((prev)=>prev+1) : setSessionCount((prev)=>prev-1)
  }
  const resetSessionCount = () => {
    setSessionCount(25)
  }
  return (
    <div className="App">
    <div id='break'>
      <h6>Break Time</h6>
      <div className='count'>{breakCount}</div>
      <div>
        <button className='up-down' onClick={()=>handleBreakCount('up')}>+</button>
        <button className='up-down' onClick={()=>handleBreakCount('down')}>-</button>
      </div>
      <button className='reset' onClick={resetBreakCount}>RESET</button>
    </div>
    <div id='session'>

      <h6>Session Time</h6>
      <div className='count'>{sessionCount}</div>
      <div>
        <button className='up-down' onClick={()=>handleSessionCount('up')}>+</button>
        <button className='up-down' onClick={()=>handleSessionCount('down')}>-</button>
      </div>

      <button className='reset' onClick={resetSessionCount}>RESET</button>
    </div>

    </div>
  );
}

export default App;
