import { useState } from "react"
import React from "react";
import './App.css';

export default function Counter (props){

    const[count, setCount] = useState(props.time)
    const handleCount = (op) =>{

      op==='up' ? setCount((prev)=>prev+1) : setCount((prev)=>prev-1)

    }
    const resetCount = () => {
      setCount(props.time)
    }
    const timer = () => props.timer(count)

    return(
      <div id='break' >
          <h2 id={props.labelId}>{props.label}</h2>
          <div id={props.lengthId} className='count' >{count}</div>
          <div id='container'>
            <button id={props.incrementId} className='up-down' onClick={()=>{
                handleCount('up')
                props.timer((count)=>count)
            }}>+</button>
            <button id={props.decrementId} className='up-down' onClick={()=>{
                handleCount('down') 
                props.timer((count)=>count)
            }}>-</button>
          </div>
          <button className='reset' onClick={resetCount}>RESET</button>
        </div>
    )

}
