import { useState } from 'react';
import './App.css';

function App() {
  const[countBreak, setCountBreak] = useState(5)

  const handleCountBreak = (op) =>{
    op==='up' ? setCountBreak((prev)=>prev+1) : setCountBreak((prev)=>prev-1)
  }
  const resetCountBreak = () => {
    setCountBreak(0)
  }
  return (
    <div className="App">
    <div id='break'>
      <p>{countBreak}</p>
      <button onClick={()=>handleCountBreak('up')}>UP</button>
      <button onClick={()=>handleCountBreak('down')}>DOWN</button>
      <button onClick={resetCountBreak}>RESET</button>
    </div>

    </div>
  );
}

export default App;
