import React, { useRef, useEffect, useState } from 'react';
import './App.css';
import Counter from './Counter';
import Countdown from 'react-countdown'


function App() {


  const [sessionTimer,setSessionTimer] = useState(25)
  const [breakTimer,setBreakTimer] = useState(5)  
  const [sessionOrBreak, setSessionOrBreak] = useState(true)
  const [running, setRunning] = useState(true)
  const clockRef = useRef();
  const render = ({ formatted: { hours, minutes, seconds }, completed }) => (<span id='time-left'>{minutes}:{seconds}</span>)

  const start = () => {
    clockRef.current.start()
    setRunning(prev=>!prev)
  }
  const pause = () => {
    clockRef.current.pause()
  }



  let sessionTime= <Countdown 
                  date={Date.now() + (sessionOrBreak?sessionTimer*60000:breakTimer*60000)} 
                  autoStart={true}
                  renderer={render}
                  ref={clockRef}
                  />







  return (
    <div className="App">
      <div id='counters'>
        <Counter 
          labelId='break-label' 
          label='Break Length' 
          time={5}
          incrementId='break-increment'
          decrementId='break-decrement'
          lengthId='break-length'
          timer={setBreakTimer}

        />
        <Counter 
          labelId='session-label' 
          label='Session Length' 
          time={25}
          incrementId='session-increment'
          decrementId='session-decrement'
          lengthId='session-length'
          timer={setSessionTimer}
        />  
      </div>
      <div id="session-container">
        <h2 id='timer-label'>S/break</h2>
        {sessionTime}
      </div>


      <button onClick={running?start:pause}>Start/Stop</button>

      <button onClick={()=>setSessionOrBreak((x)=>!x)}>Reset</button>
    </div>
  );
}

export default App;
