import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useState } from 'react';
import './App.css';
import Counter from './Counter';

function App() {

  const[sessionCount, setSessionCount] = useState(25)

  const handleBreakCount = (op) =>{
    op==='up' ? setBreakCount((prev)=>prev+1) : setBreakCount((prev)=>prev-1)
  }
  const resetBreakCount = () => {
    setBreakCount(5)
  }
  const handleSessionCount = (op) =>{
    op==='up' ? setSessionCount((prev)=>prev+1) : setSessionCount((prev)=>prev-1)
  }
  const resetSessionCount = () => {
    setSessionCount(25)
  }


  return (
    <div className="App">
      <div id='counters'>
        <Counter />
        <Counter />



      </div>
    </div>
  );
}

export default App;
