import React, { useRef, useEffect, useState } from 'react';
import './App.css';
import Countdown from 'react-countdown'
import NotificationSound from './alert.mp3'



function App() {


  const [sessionTimer,setSessionTimer] = useState(25)
  const [breakTimer,setBreakTimer] = useState(5)  
  const [sessionOrBreak, setSessionOrBreak] = useState(true)
  const [running, setRunning] = useState(false)
  const clockRef = useRef();
  const render = ({ formatted: { hours, minutes, seconds }, completed }) => (<span id='time-left'>{minutes}:{seconds}</span>)
  const audioPlayer = useRef(null);

  const play = () =>audioPlayer.current.play()

  useEffect(()=>{
    if(running){
      clockRef.current.start()
    }else{
      clockRef.current.pause()
    }
  },[running])



  let sessionTime= <Countdown 
                  date={Date.now() + (sessionOrBreak?sessionTimer*60000:breakTimer*60000)} 
                  autoStart={running?true:false}
                  renderer={render}
                  ref={clockRef}
                  onComplete={

                    ()=>{
                      setSessionOrBreak((x)=>!x)
                      clockRef.current.start()
                      play()
                    }
                  }
                  />







  return (
    <div className="App">
      <div id='counters'>
        <Counter 
          labelId='break-label' 
          label='Break Length' 
          time={5}
          incrementId='break-increment'
          decrementId='break-decrement'
          lengthId='break-length'
          timer={setBreakTimer}

        />
        <Counter 
          labelId='session-label' 
          label='Session Length' 
          time={25}
          incrementId='session-increment'
          decrementId='session-decrement'
          lengthId='session-length'
          timer={setSessionTimer}
        />  
      </div>
      <div id="session-container">
        <h2 id='timer-label'>{sessionOrBreak?'Session':'Break'}</h2>
        <div id='time-left'>{sessionTime}</div>
      </div>


      <button id='start_stop' onClick={()=>setRunning((prev)=>!prev)}>Start/Stop</button>

      <button id='reset' onClick={
        ()=>{
          setSessionTimer(25)
          setBreakTimer(5)
          setRunning(false)
          setSessionOrBreak(true)
        }
      }>Reset</button>

      <audio ref={audioPlayer} src={NotificationSound} />
    </div>
  );
}

const Counter = (props) => {

  const[count, setCount] = useState(props.time)







  const handleCount = (op) =>{
      if(count>=0){
          setCount((prev)=>op==='up'?prev+1:prev-1) 
          props.timer(count)
      }


  }
  const resetCount = () => {
      setCount(props.time)
  }


  return(
    <div id='break' >
        <h2 id={props.labelId}>{props.label}</h2>
        <div id={props.lengthId} className='count' >{count}</div>
        <div id='container'>
          <button id={props.incrementId} className='up-down' onClick={
              ()=>{

                      handleCount('up')
                      props.timer((prev)=>prev+1) 

              }
          }>+</button>
          <button id={props.decrementId} className='up-down' onClick={
              ()=>{
                  if(count>1){
                      handleCount('down')
                      props.timer((prev)=>prev-1)
                  }


              }
          }>-</button>
        </div>
      </div>
  )

}

export default App;
